import React from 'react';
import { Formik, Field, Form, ErrorMessage } from 'formik';
import * as Yup from 'yup';
import styled from 'styled-components'

const FormWrapper = styled.div`
display: flex;
flex-direction: column;
justify-content: center;
align-items: center;
`

const H1 = styled.h1`
width: 175px;
height: 39px;
font-weight: 700;
font-size: 32px;
line-height: 39px;
color: rgba(0, 0, 0, 0.64);
`

const Description = styled.div`
width: 288px;
height: 57px;
font-weight: 500;
font-size: 16px;
line-height: 19px;
color: rgba(0, 0, 0, 0.45);
`

const Email = styled.input`
width: 288px;
height: 59px;
background: #FFFFFF;
border: 1px solid #CBD5E0;
border-radius: 6px;
`

const Message = styled.input`
width: 288px;
height: 115px;
background: #FFFFFF;
border: 1px solid rgba(0, 0, 0, 0.2);
border-radius: 6px;
`

const ContactForm = () => {
  return (
    <div>
      <H1>Contact Us</H1>
      <Description>If you have any questions please <br /> don't hesitate to contact us.<br /> We're here to help!</Description>
    <Formik

      initialValues={{ email: '', message: '' }}

      validationSchema={Yup.object({
        email: Yup.string().email('Invalid email address').required('Required'),
        message: Yup.string()
          .max(15, 'Must be 15 characters or less')
          .required('Required')

      })}

      onSubmit={async (values) => {
        await new Promise((r) => setTimeout(r, 500));
        alert(JSON.stringify(values, null, 2));
      }}
    >
      <Form>

        <Email name="email" type="email" placeholder="Your email" />
        <ErrorMessage name="email" />

        <Message name="message" type="text" as='textarea' placeholder="Your message" />
        <ErrorMessage name="message" />

        <button type="submit">Send</button>
      </Form>
    </Formik>
    </div>
  );
};

export default ContactForm;
